        <script>
            function toggleAccordion(index) {
                // Ambil semua elemen accordion body
                const allBodies = document.querySelectorAll('[id^="accordion-flush-body-"]');
                const allIcons = document.querySelectorAll('[id^="accordion-flush-heading-"] svg');
        
                // Loop melalui semua elemen untuk menutup accordion lainnya
                allBodies.forEach((body, i) => {
                    const icon = allIcons[i];
                    if (i !== index) {
                        body.classList.add('hidden');
                        icon.classList.remove('rotate-180');
                        body.previousElementSibling.setAttribute('aria-expanded', 'false');
                    }
                });
        
                // Sekarang, buka/tutup accordion yang diklik
                const body = document.getElementById('accordion-flush-body-' + index);
                const icon = body.previousElementSibling.querySelector('svg');
                
                if (body.classList.contains('hidden')) {
                    body.classList.remove('hidden');
                    icon.classList.add('rotate-180'); // Rotasi saat dibuka
                    body.previousElementSibling.setAttribute('aria-expanded', 'true');
                } else {
                    body.classList.add('hidden');
                    icon.classList.remove('rotate-180'); // Kembali ke posisi semula saat ditutup
                    body.previousElementSibling.setAttribute('aria-expanded', 'false');
                }
            }
        </script>
